function HideThisDiv(i){jQuery(i).hide()}function AppendVideo(i){jQuery("div#"+i).append('<div class="video-youtube"><div class="module-wrap"><iframe width="420" height="315" id="video_iframe" src="https://www.youtube.com/embed/'+i+'?autoplay=1" frameborder="0" allowfullscreen></iframe></div></div>')}jQuery("div.module-video").each(function(){var i=this.id;jQuery("#video_play_"+i).on("click",function(){HideThisDiv("#video_image_"+i),AppendVideo(i)}),jQuery("#thumbnail_"+i).on("click",function(){HideThisDiv("#video_image_"+i),AppendVideo(i)})});